import "core-js/modules/es.array.unshift.js";
import jwtPrase from '@/assets/js/jwtPrase';
export default {
  name: "StudentMsgExportForm",
  data() {
    return {
      exportForm: {
        major: '',
        year: '',
        number: 10
      },
      //数据最大条数
      max: '',
      majorList: []
    };
  },
  methods: {
    //获取毕业年份选择项

    //获取majorList
    async getMajorList() {
      let college = jwtPrase().college;
      const resp = await this.$http.get('/dict/major/college/' + college);
      if (resp.data.code === 200) {
        this.majorList = resp.data.data;
        this.majorList.unshift({
          major: '0',
          description: '全部专业'
        });
      }
    }
    //获取
  },

  created() {
    this.getMajorList();
  }
};
