export default {
  name: "DialogStuInfo",
  props: ["formType", "sId", 'dialogVisible'],
  emits: ['closeDialog'],
  data() {
    return {
      visible: false,
      title: '',
      infoDetail: {
        sid: '',
        sname: "",
        gender: "",
        password: "",
        graduationYear: "",
        college: "",
        major: "",
        phone: "",
        email: "",
        topicName: ''
      },
      genderList: '',
      collegeList: '',
      majorList: '',
      rules: {
        sid: [{
          required: true,
          message: '学号不能为空',
          trigger: 'blur'
        }],
        sname: [{
          required: true,
          message: '学生姓名不能为空',
          trigger: 'blur'
        }],
        gender: [{
          required: true,
          message: '请选择学生性别',
          trigger: 'change'
        }],
        password: [{
          required: true,
          message: '密码不能为空',
          trigger: 'blur'
        }],
        graduationYear: [{
          required: true,
          message: '毕业年份不能为空',
          trigger: 'blur'
        }],
        college: [{
          required: true,
          message: '请选择学院',
          trigger: 'change'
        }],
        major: [{
          required: true,
          message: '请选择专业名称',
          trigger: 'change'
        }],
        phone: [{
          required: true,
          message: '请输入手机号',
          trigger: 'blur'
        }, {
          length: 11,
          message: '手机号长度为11位',
          trigger: 'change'
        }],
        email: [{
          required: true,
          message: '请输入邮箱账号',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          // 发送请求
          if (this.formType == '0') {
            //新增
            this.$confirm('此操作将新增毕业生信息, 是否继续?', '温馨提示', {
              confirmButtonText: '确定',
              cancelButtonText: '取消',
              type: "success"
            }).then(() => {
              // 发送新增请求
              this.addUser();
            }).catch(() => {
              this.$message({
                type: 'info',
                message: '已取消新增'
              });
            });
          } else if (this.formType == '1') {
            //修改
            this.$confirm('此操作将修改毕业生信息, 是否继续?', '温馨提示', {
              confirmButtonText: '确定',
              cancelButtonText: '取消',
              type: "success"
            }).then(() => {
              // 发送修改请求
              this.updataUser();
            }).catch(() => {
              this.$message({
                type: 'info',
                message: '已取消修改'
              });
            });
          }
        } else {
          return false;
        }
      });
    },
    // 关闭dialog
    closeDialogEmit() {
      this.visible = false;
      this.$emit('closeDialog', this.visible);
    },
    //发送请求获取collegeList
    async getCollegeList() {
      const resp = await this.$http.get('/dict/college');
      if (resp.data.code === 200) {
        this.collegeList = resp.data.data;
      }
    },
    //发送请求获取genderList
    async getGenderList() {
      const resp = await this.$http.get('/dict/gender');
      if (resp.data.code === 200) {
        this.genderList = resp.data.data;
      }
    },
    //发送请求获取majorList
    async getMajorList(id) {
      const resp = await this.$http.get('/dict/major/college/' + id);
      if (resp.data.code === 200) {
        this.majorList = resp.data.data;
      }
    }
  },
  watch: {
    dialogVisible(newval) {
      this.visible = newval;
    },
    'infoDetail.college'(newval) {
      //根据newval发送请求获取majorList
      this.getMajorList(newval);
    }
  },
  updated() {
    if (this.formType === '1') {
      this.title = '修改学生信息';
      //使用sId发送请求，获取sId对应学生的全部信息
      console.log("请求sID对应学生的信息" + this.sId);
    } else {
      this.title = '新增学生信息';
    }
  },
  created() {
    //发送请求获取genderList
    this.getGenderList();
    //发送请求获取collegeList
    this.getCollegeList();
  }
};
