import "core-js/modules/es.array.push.js";
export default {
  name: "LoginForm",
  props: ["accountName", "userType"],
  data() {
    var validatePassword = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请输入密码'));
      } else {
        callback();
      }
    };
    var validateAccount = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请输入' + this.$props.accountName));
      } else {
        callback();
      }
    };
    return {
      formData: {
        password: '',
        account: ''
      },
      rules: {
        password: [{
          validator: validatePassword,
          trigger: 'blur'
        }],
        account: [{
          validator: validateAccount,
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          //前端校验通过，根据type选择相应接口 发送ajax请求
          if (this.userType == '0') {
            //毕业生登录
            alert('submit!' + this.userType);
          } else if (this.userType == '1') {
            //导师登录
            alert('submit!' + this.userType);
          } else if (this.userType == '2') {
            //管理员登录
            this.adminLogin();
          }
        } else {
          return false;
        }
      });
    },
    async adminLogin() {
      const resp = await this.$http.get("/userAdmin/login/" + this.formData.account + "/" + this.formData.password);
      if (resp.data.code === 200) {
        //验证通过
        //1.全局存储jwt

        //2.跳转到首页
        this.$message({
          message: "欢迎使用高校智能毕业设计后台管理系统！",
          type: 'success'
        });
        this.toHome();
      } else if (resp.data.code === 401) {
        //身份验证失败
        this.$alert("账号或密码错误！", {
          type: "warning"
        });
      } else {
        //其他异常
        this.$alert("系统出错了，请稍后再尝试登录。", {
          type: "warning"
        });
      }
    },
    toHome() {
      //如果身份验证成功，跳转到timeLine
      this.$router.push({
        name: 'timeLine'
      });
    },
    resetForm(formName) {
      this.$refs[formName].resetFields();
    }
  }
};
