import jwtPrase from '@/assets/js/jwtPrase';
export default {
  name: "UpdateUserMsg",
  props: ["visible"],
  emits: ['close'],
  data() {
    return {
      ruleForm: {
        id: '',
        name: '',
        phone: '',
        email: ''
      },
      rules: {
        phone: [{
          required: true,
          message: '请输入手机号',
          trigger: 'blur'
        }, {
          min: 11,
          max: 11,
          message: '长度为11位数字',
          trigger: 'blur'
        }],
        email: [{
          required: true,
          message: '请输入邮箱',
          trigger: 'blur'
        }]
      },
      dialogVisible: false
    };
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          //验证成功，发送aixos请求
          this.updateMsg();
        } else {
          return false;
        }
      });
    },
    resetForm(formName) {
      this.$refs[formName].resetFields();
    },
    closeDialog() {
      this.dialogVisible = false;
      this.$emit('close', false);
    },
    async getMsg() {
      //获取用户信息
      let id = jwtPrase().id;
      let name = jwtPrase().name;
      let userType = jwtPrase().userType;
      let url;
      if (userType == 0) {
        //毕业生
        url = "/userStudent/";
      } else if (userType == 1) {
        //导师
        url = "/userTutor/";
      } else if (userType == 2) {
        //管理员
        url = "/userAdmin/";
      }
      const resp = await this.$http.get(url + id);
      if (resp.data.code === 200) {
        let user = resp.data.data;
        this.ruleForm.id = id;
        this.ruleForm.name = name;
        this.ruleForm.phone = user.phone;
        this.ruleForm.email = user.email;
      }
    },
    async updateMsg() {
      let userType = jwtPrase().userType;
      let url;
      if (userType == 0) {
        //毕业生
        url = "/userStudent";
      } else if (userType == 1) {
        //导师
        url = "/userTutor";
      } else if (userType == 2) {
        //管理员
        url = "/userAdmin";
      }
      url += "/updateMsg";
      const resp = await this.$http({
        method: 'put',
        url: url,
        data: this.ruleForm
      });
      if (resp.data.code === 200) {
        let result = resp.data;
        this.$message({
          message: result.msg,
          type: 'success'
        });
        this.getMsg();
      }
    }
  },
  watch: {
    visible(newValue) {
      this.dialogVisible = newValue;
      this.getMsg();
    }
  },
  created() {
    this.getMsg();
  }
};
